package com.brkv.travelfinder

import android.content.Context
import android.content.SharedPreferences
import android.os.Bundle
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.fragment.app.Fragment
import androidx.fragment.app.FragmentManager
import androidx.fragment.app.FragmentTransaction
import com.brkv.travelfinder.fragments.Card
import com.google.android.material.tabs.TabLayout
import org.jetbrains.anko.doAsync


class MainActivity : AppCompatActivity() {
    companion object { const val PREF_NB = "PREF_NB" }
    val articleDb by lazy { ArticleDb(ArticleDbHelper(applicationContext)) }
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        val tabLayout = findViewById<View>(R.id.tabLayout) as TabLayout
        val sharedPref = getPreferences(Context.MODE_PRIVATE)
        val editor: SharedPreferences.Editor = sharedPref.edit()
        changeFrag(Card(query("Europe")))

        if(sharedPref.contains(PREF_NB)){
            editor.putInt(PREF_NB, sharedPref.getInt(PREF_NB,0)+1)
            editor.commit()
        }
        else {
            editor.putInt(PREF_NB, 0)
            initDb()
            editor.commit()
        }

        tabLayout.addOnTabSelectedListener(object : TabLayout.OnTabSelectedListener {

            override fun onTabSelected(tab: TabLayout.Tab?) {
                var fragment: Fragment = Card(query("Europe"))
                when (tab!!.position) {
                    0 -> fragment = Card(query("Europe"))
                    1 -> fragment = Card(query("Asie"))
                    2 -> fragment = Card(query("Amérique"))
                    3 -> fragment = Card(query("Afrique"))
                    4 -> fragment = Card(query("Océanie"))
                }
                changeFrag(fragment)
            }


            override fun onTabReselected(tab: TabLayout.Tab?) {

            }

            override fun onTabUnselected(tab: TabLayout.Tab?) {

            }
        })
    }

    private fun changeFrag(fragment : Fragment){
        val fm: FragmentManager = supportFragmentManager
        val ft: FragmentTransaction = fm.beginTransaction()
        ft.replace(R.id.frameLayout, fragment)
        ft.setTransition(FragmentTransaction.TRANSIT_FRAGMENT_OPEN)
        ft.commit()
    }

    private fun initDb() {
        doAsync {
            var article1 = Article("Londres","Londres est la capitale et la plus grande ville d'Angleterre et du Royaume-Uni. La ville est située près de l'estuaire de la Tamise dans le sud-est de l'Angleterre. La ville exerce un impact considérable sur les arts, le commerce, l'éducation, le divertissement, la mode, les finances, les soins de santé, les médias, les services professionnels, la recherche et le développement, le tourisme et les transports.", "https://images.pexels.com/photos/460672/pexels-photo-460672.jpeg", "London", "Europe")
            var article2 = Article("Paris","Paris est la ville la plus peuplée et la capitale de la France. Elle se situe au cœur d'un vaste bassin sédimentaire aux sols fertiles et au climat tempéré, le bassin parisien, sur une boucle de la Seine, entre les confluents de celle-ci avec la Marne et l'Oise. Paris est également le chef-lieu de la région Île-de-France et le centre de la métropole du Grand Paris, créée en 2016. Elle est divisée en arrondissements, comme les villes de Lyon et de Marseille, au nombre de vingt. ", "https://images.pexels.com/photos/149637/pexels-photo-149637.jpeg", "Paris", "Europe")
            var article3 = Article("Berlin","Berlin est la capitale et la plus grande ville d'Allemagne. Institutionnellement, c’est une ville-État nommée Land de Berlin. Située dans le Nord-Est du pays, Berlin compte environ 3,8 millions d'habitants. Ses habitants s'appellent les Berlinois et les Berlinoises (die Berliner et die Berlinerinnen en allemand). Elle est la première ville et la huitième agglomération la plus peuplée de l'Union européenne.", "https://images.pexels.com/photos/1057840/pexels-photo-1057840.jpeg", "Berlin", "Europe")
            var article4 = Article("Amsterdam","Amsterdam est la capitale des Pays-Bas, bien que le gouvernement ainsi que la plupart des institutions nationales siègent à La Haye. Sur la base des chiffres de l'année 2017, la commune d'Amsterdam compte plus de 850 000 habitants, appelés Amstellodamois, ce qui en fait la commune néerlandaise la plus peuplée. ", "https://images.pexels.com/photos/2031706/pexels-photo-2031706.jpeg", "Amsterdam", "Europe")
            var article5 = Article("Venise","Venise est une ville côtière du nord-est de l'Italie, sur les rives de la mer Adriatique. Elle s'étend sur un ensemble de 121 petites îles séparées par un réseau de canaux et reliées par 435 ponts. Située au milieu de la lagune vénète, entre les estuaires du Pô et du Piave, Venise est renommée pour cet emplacement exceptionnel ainsi que pour son architecture et son patrimoine culturel, qui lui valent une inscription au patrimoine mondial de l'UNESCO.", "https://images.pexels.com/photos/1796736/pexels-photo-1796736.jpeg", "Venice", "Europe")
            var article6 = Article("Barcelone","Barcelone est la capitale administrative et économique de la Catalogne, de la province de Barcelone, de la comarque du Barcelonès ainsi que de son aire et de sa région métropolitaines, en Espagne. Barcelone est la deuxième ville d'Espagne au regard de la population, de l'économie et des activités, la onzième ville la plus peuplée de l'Union européenne et la sixième en incluant sa banlieue : en 2018, 4,84 millions de personnes vivent dans l'agglomération barcelonaise. La majeure partie des municipalités adjacentes sont en outre rassemblées dans l'Aire métropolitaine de Barcelone. \n", "https://images.pexels.com/photos/1388030/pexels-photo-1388030.jpeg", "Barcelona", "Europe")
            var article7 = Article("Prague","Prague est la capitale et la plus grande ville de la Tchéquie, en Bohême. Située au cœur de l'Europe centrale, à l'ouest du pays, la ville est édifiée sur les rives de la Vltava (en allemand : Moldau). Capitale historique du royaume de Bohême, berceau du peuple tchèque, Prague connaît son apogée au XIVe siècle, sous le règne du roi de Bohême et empereur germanique Charles IV qui en fait la capitale de l'Empire. Elle est alors un centre culturel et religieux de première importance.", "https://images.pexels.com/photos/61381/pexels-photo-61381.jpeg", "Prague", "Europe")
            var article8 = Article("Budapest","Budapest est la plus grande ville et la capitale de la Hongrie. Elle se situe en aval du coude du Danube entre le massif de Transdanubie et l'Alföld. Ses habitants sont les Budapestois. La ville actuelle est créée en 1873 par la fusion de Buda — alors capitale de la Hongrie — de Pest et d'Óbuda. Elle a pour origine le site d'Aquincum, un point de peuplement celte devenu capitale de la Pannonie inférieure pendant l'époque romaine.", "https://images.pexels.com/photos/732057/pexels-photo-732057.jpeg", "Budapest", "Europe")
            var article9 = Article("Lisbonne","Lisbonne est la capitale et la plus grande ville du Portugal. Considérée comme une « ville mondiale » selon le classement 2010 du Réseau d’étude sur la mondialisation et les villes mondiales comme Taipei, Miami, Varsovie ou Munich, Lisbonne est également le chef-lieu du district de Lisbonne, du Grand Lisbonne et de la zone métropolitaine de Lisbonne. Ses habitants sont habituellement appelés Lisboètes (du portugais lisboeta), mais on rencontre aussi les termes Lisbonnin et Lisbonnais. ", "https://images.pexels.com/photos/9253/sea-city-landscape-sky.jpg", "Lisbon", "Europe")
            var article10 = Article("Reykjavik","Reykjavik est la capitale de l'Islande. Elle se situe à environ 250 km au sud du cercle polaire arctique, ce qui en fait la capitale la plus septentrionale d'un État souverain (la capitale du Groenland, Nuuk, étant située quelques kilomètres plus au nord). Elle s'étale entre deux fjords, dans une zone comptant de nombreuses sources chaudes, le long d'une baie. Elle est ainsi considérée comme la ville la « plus verte du monde » : on y compte environ 410 m2 d'espaces verts par habitant.", "https://images.pexels.com/photos/831058/pexels-photo-831058.jpeg", "Reykjavik", "Europe")
